import { Router } from '@angular/router';
import { Component, ElementRef, ViewChild, inject } from '@angular/core';
import { UploadService } from 'src/app/upload.service';

@Component({
  selector: 'app-uploadpage',
  templateUrl: './uploadpage.component.html',
  styleUrls: ['./uploadpage.component.css']
})
export class UploadpageComponent {

  @ViewChild('files') files !: ElementRef;

  fileList : string[] = [];


  router = inject(Router)
  uploadSvc = inject(UploadService)

  uploadMusic() {
    this.router.navigate(['/playmusic'])
  }

  listFiles(event: any) {
     event.target.value.forEach((value : File) => {
      if (!this.fileList.includes(value.name)) {
        this.fileList.push(value.name)
      }
     })

  }

}
  <h1> Upload Your Music Files Here!</h1>

  <input type="file" placeholder="Upload your files here" multiple #files (change)="listFiles($event)">
  <br>
  <button (submit)="uploadMusic()" > Submit files </button>

  <p *ngIf="fileList">
    <span **ngFor="let item of list"> {{item}} </span>
  </p>
